mapper.select:
select * from metoo_terminal terminal



        OR terminal.v4ip
        like
        concat(concat('%',#{filter}),'%')
        or  terminal.v4ip1
        like concat(concat('%',#{filter}),'%')
        or  terminal.v4ip2
        like concat(concat('%',#{filter}),'%')
        or  terminal.v4ip3
        like concat(concat('%',#{filter}),'%')
        or  terminal.v6ip
        like concat(concat('%',#{filter}),'%')
        or  terminal.v6ip1
        like concat(concat('%',#{filter}),'%')
        or  terminal.v6ip2
        like concat(concat('%',#{filter}),'%')
        or  terminal.v6ip3
        like concat(concat('%',#{filter}),'%')
        or terminal.name
        like concat(concat('%',#{filter}),'%')
        or terminal.mac
        like concat(concat('%',#{filter}),'%')
        OR terminal.asset_number
        LIKE CONCAT('%', #{filter}, '%')
        OR terminal.serial_number
        LIKE CONCAT('%', #{filter}, '%')


        AND hostname = #{hostname}


        AND client_hostname = #{client_hostname}


        AND online = #{online}


        AND asset_number = #{asset_number}


        AND serial_number = #{serial_number}


        AND deviceTypeId = #{deviceTypeId}


        AND purchase_time BETWEEN #{start_purchase_time} AND #{end_purchase_time}


        AND warranty_time BETWEEN #{start_warranty_time} AND #{end_warranty_time}


        AND projectId = #{projectId}


        AND vendorId = #{vendorId}


        AND type = #{type}


        AND deviceUuid = #{deviceUuid}


ORDER BY ISNULL(v4ip) or v4ip = '', INET_ATON(v4ip) ASC

select * from metoo_terminal


        AND deviceIp = #{deviceIp}


        AND deviceUuid = #{deviceUuid}


        AND deviceIp2 = #{deviceIp2}


        AND mac = #{mac}


        AND macVendor = #{macVendor}


        AND v4ip = #{v4ip}


        AND v6ip = #{v6ip}


        AND hostname = #{hostname}


        AND client_hostname = #{client_hostname}


        AND remoteDevice = #{remoteDevice}


        AND asset_number = #{asset_number}


        AND tag = #{tag}


        AND tag is null


        AND v4ip IS NULL


        AND deviceType = #{deviceType}


        AND deviceTypeId != #{notDeviceTypeId}


        AND tag != #{notTag}



        AND tag in

            #{item}



        AND mac not in

            #{item}



        AND v4ip not in

            #{item}



        AND `id` != #{terminalId}


        AND online = #{online}


        OR deviceUuid2 = #{deviceUuid2}


        OR (deviceUuid2 = #{deviceUuidAndDeviceTypeId} and deviceTypeId = 34)


ORDER BY ISNULL(v4ip) or v4ip = '', INET_ATON(v4ip) ASC

select * from metoo_terminal_history


        AND deviceIp = #{deviceIp}


        AND deviceUuid = #{deviceUuid}


        AND deviceIp2 = #{deviceIp2}


        OR deviceUuid2 = #{deviceUuid2}


        or (deviceUuid2 = #{deviceUuidAndDeviceTypeId} and deviceTypeId = 34)


        AND mac = #{mac}


        AND online = #{online}


        AND addTime = (
        SELECT
        addTime
        FROM
        metoo_terminal_history
        WHERE
        addTime <= #{time}
        ORDER BY addTime DESC limit 1
        )


ORDER BY ISNULL(v4ip) or v4ip = '', INET_ATON(v4ip) ASC

mapper.update:
         UPDATE metoo_terminal d1
         JOIN (
                SELECT *
                FROM metoo_terminal
                WHERE macVendor
                LIKE 'VMware%'
            )d2
        ON d1.id = d2.id
        SET d1.deviceTypeId = 9;
--       update metoo_terminal d1 where EXISTS(select 1 from metoo_terminal d2 where d2.macVendor like 'VMware%' and d1.id = d2.id);

-- UPDATE metoo_terminal d1 -- JOIN ( -- SELECT deviceName, v4ip -- -- 子查询：选择所有 macVendor 为非 'VMware' 的记录，排除空值，确保 v4ip 是唯一的 -- FROM metoo_terminal -- WHERE macVendor NOT LIKE '%VMware%' AND macVendor IS NOT NULL AND v4ip IS NOT NULL -- GROUP BY deviceName, port, v4ip -- HAVING COUNT(*) = 1 -- ) AS d2 -- ON d1.deviceName = d2.deviceName -- -- 仅更新 macVendor 为 'VMware' 的条目 -- SET d1.deviceIp = d2.v4ip, d1.deviceName = null, d1.hostname = null, d1.deviceTypeId = 9, d1.tag = 'VDT' -- WHERE d1.macVendor LIKE '%VMware%'; -- 所有操作在同一个 SQL 块中执行 BEGIN;
-- 创建临时表 CREATE TEMPORARY TABLE tmp_table AS -- SELECT deviceName, port, v4ip -- FROM metoo_terminal t1 -- WHERE t1.macVendor NOT LIKE 'VMware%' -- AND EXISTS ( -- SELECT 1 -- FROM metoo_terminal t2 -- WHERE t1.deviceName = t2.deviceName -- AND t1.port = t2.port -- GROUP BY t2.deviceName, t2.port -- HAVING COUNT(CASE WHEN t2.macVendor LIKE 'VMware%' THEN 1 END) >= 1 -- AND COUNT(CASE WHEN t2.macVendor NOT LIKE 'VMware%' OR t2.macVendor IS NULL THEN 1 END) = 1 -- );
SELECT * FROM( SELECT * FROM metoo_terminal t1 WHERE EXISTS ( SELECT 1 FROM metoo_terminal t2 WHERE t1.deviceName = t2.deviceName AND t1.port = t2.port GROUP BY t2.deviceName, t2.port HAVING COUNT(CASE WHEN t2.macVendor LIKE 'VMware%' THEN 1 END) >= 1 AND COUNT(CASE WHEN t2.macVendor NOT LIKE 'VMware%' OR t2.macVendor IS NULL THEN 1 END) = 1 )ORDER BY deviceName DESC )t3 WHERE t3.macVendor NOT LIKE 'VMware%' OR t3.macVendor IS NULL;
-- 执行更新操作 UPDATE metoo_terminal d1 JOIN tmp_table d2 ON d1.deviceName = d2.deviceName AND d1.port = d2.port SET d1.deviceIp = d2.v4ip, -- d1.deviceName = NULL, -- d1.hostname = NULL, d1.tag = 'VDT' WHERE d1.macVendor LIKE 'VMware%';
-- 删除临时表 DROP TEMPORARY TABLE tmp_table;
COMMIT;
